const index = "import { Component } from 'solid-js';\nimport { useFormHandler } from 'solid-form-handler';\nimport { yupSchema } from 'solid-form-handler/yup';\nimport * as yup from 'yup';\nimport { Checkbox } from '@components';\n\ntype Schema = {\n  acceptPolicy: boolean;\n  acceptTerms: boolean;\n  accountStatus: 'active' | 'inactive';\n};\n\nconst schema: yup.Schema<Schema> = yup.object({\n  acceptPolicy: yup\n    .boolean()\n    .oneOf([true], 'Accept policy is required')\n    .required()\n    .typeError('Accept policy is required'),\n  acceptTerms: yup.boolean().required().default(false),\n  accountStatus: yup\n    .mixed<Schema['accountStatus']>()\n    .oneOf(['active', 'inactive'])\n    .required()\n    .default('inactive'),\n});\n\nexport const Form: Component = () => {\n  const formHandler = useFormHandler<Schema>(yupSchema(schema));\n  const { formData } = formHandler;\n\n  const submit = async (event: Event) => {\n    event.preventDefault();\n    try {\n      await formHandler.validateForm();\n      alert('Data sent with success: ' + JSON.stringify(formData()));\n      formHandler.resetForm();\n    } catch (error) {\n      console.error(error);\n    }\n  };\n\n  const reset = () => {\n    formHandler.resetForm();\n  };\n\n  const fill = () => {\n    formHandler.fillForm({\n      acceptPolicy: true,\n      acceptTerms: false,\n      accountStatus: 'active',\n    });\n  };\n\n  return (\n    <form autocomplete=\"off\" onSubmit={submit}>\n      <h4 class=\"mb-3\">Using yup schema</h4>\n      <div class=\"mb-3\">\n        <Checkbox\n          label=\"Accept policy\"\n          name=\"acceptPolicy\"\n          formHandler={formHandler}\n        />\n      </div>\n      <div class=\"mb-3\">\n        <Checkbox\n          label=\"Accept terms\"\n          name=\"acceptTerms\"\n          formHandler={formHandler}\n          display=\"switch\"\n        />\n      </div>\n      <div class=\"mb-3\">\n        <Checkbox\n          label={\n            formData().accountStatus === 'active'\n              ? 'Account active'\n              : 'Account inactive'\n          }\n          value=\"active\"\n          uncheckedValue=\"inactive\"\n          name=\"accountStatus\"\n          formHandler={formHandler}\n          display=\"switch\"\n        />\n      </div>\n      <div class=\"mb-3\">\n        <button class=\"btn btn-primary me-2\">Submit</button>\n        <button class=\"btn btn-primary me-2\" onClick={reset} type=\"button\">\n          Reset\n        </button>\n        <button class=\"btn btn-primary me-2\" onClick={fill} type=\"button\">\n          Fill\n        </button>\n      </div>\n      <p>\n        <b>Form data:</b>\n      </p>\n      <pre class=\"mt-3 border bg-light p-3\">\n        <code>{JSON.stringify(formData(), null, 2)}</code>\n      </pre>\n    </form>\n  );\n};\n";

export { index as default };
