const index = "import { Component, For, JSX, Show } from 'solid-js';\nimport { NavLink } from '@solidjs/router';\nimport { MenuItem, Size } from '@interfaces';\nimport './index.css';\n\nexport interface NavbarProps {\n  menu?: MenuItem[];\n  brand?: JSX.Element;\n  rightMenu?: Omit<MenuItem, 'text' | 'section'>[];\n  size?: Size;\n}\n\nexport const Navbar: Component<NavbarProps> = (props) => (\n  <nav class=\"sticky-top navbar navbar-dark bg-dark navbar-expand-lg\">\n    <div class={`container-${props.size || 'fluid'}`}>\n      {props.brand}\n      <button\n        class=\"navbar-toggler\"\n        type=\"button\"\n        data-bs-toggle=\"collapse\"\n        data-bs-target=\"#navbarScroll\"\n        aria-controls=\"navbarScroll\"\n        aria-expanded=\"false\"\n        aria-label=\"Toggle navigation\"\n      >\n        <span class=\"navbar-toggler-icon\"></span>\n      </button>\n      <div\n        class=\"collapse navbar-collapse align-center justify-space-between\"\n        id=\"navbarScroll\"\n      >\n        <ul\n          class=\"navbar-nav me-auto my-2 my-lg-0 navbar-nav-scroll\"\n          style=\"--bs-scroll-height: 100px;\"\n        >\n          <For each={props.menu}>\n            {(item) => (\n              <li class=\"nav-item\">\n                <NavLink class=\"nav-link\" href={item.route as string}>\n                  {item.text}\n                </NavLink>\n              </li>\n            )}\n          </For>\n        </ul>\n        <Show when={props.rightMenu}>\n          <ul\n            class=\"navbar-nav right-menu my-2 my-lg-0 navbar-nav-scroll\"\n            style=\"--bs-scroll-height: 100px;\"\n          >\n            <For each={props.rightMenu}>\n              {(item) => (\n                <li class=\"nav-item\">\n                  <NavLink\n                    class=\"nav-link\"\n                    href={item.route as string}\n                    target={item.external ? 'blank' : undefined}\n                  >\n                    {item.icon}\n                  </NavLink>\n                </li>\n              )}\n            </For>\n          </ul>\n        </Show>\n      </div>\n    </div>\n  </nav>\n);\n";

export { index as default };
