const index = "import { Component, For, Show } from 'solid-js';\nimport { Field, useFormHandler } from 'solid-form-handler';\nimport { zodSchema } from 'solid-form-handler/zod';\nimport { z } from 'zod';\n\ntype SelectableOption = {\n  value: number | string;\n  label: string;\n};\n\nconst schema = z.object({\n  favoriteFoods: z.array(z.number()).min(2),\n});\n\nconst favoriteFoods: SelectableOption[] = [\n  { value: 1, label: 'Pizza' },\n  { value: 2, label: 'Hot Dog' },\n  { value: 3, label: 'Hamburger' },\n  { value: 4, label: 'Ice Cream' },\n  { value: 5, label: 'Sushi' },\n];\n\nexport const Form: Component = () => {\n  const formHandler = useFormHandler(zodSchema(schema));\n  const { formData } = formHandler;\n\n  const submit = async (event: Event) => {\n    event.preventDefault();\n    try {\n      await formHandler.validateForm();\n      alert('Data sent with success: ' + JSON.stringify(formData()));\n      formHandler.resetForm();\n    } catch (error) {\n      console.error(error);\n    }\n  };\n\n  const reset = () => {\n    formHandler.resetForm();\n  };\n\n  const fill = () => {\n    formHandler.fillForm({ favoriteFoods: [1, 4] });\n  };\n\n  return (\n    <form autocomplete=\"off\" onSubmit={submit}>\n      <h4 class=\"mb-3\">Using zod schema</h4>\n      <div class=\"mb-3\">\n        <Field\n          mode=\"checkbox-group\"\n          name=\"favoriteFoods\"\n          formHandler={formHandler}\n          render={(field) => (\n            <>\n              <For each={favoriteFoods}>\n                {(favoriteFood, i) => (\n                  <div\n                    class=\"form-check\"\n                    classList={{\n                      'is-invalid': field.helpers.error,\n                    }}\n                  >\n                    <input\n                      {...field.props}\n                      class=\"form-check-input\"\n                      id={`${field.props.id}-${i()}`}\n                      checked={field.helpers.isChecked(favoriteFood.value)}\n                      classList={{\n                        'is-invalid': field.helpers.error,\n                      }}\n                      type=\"checkbox\"\n                      value={favoriteFood.value}\n                    />\n                    <label\n                      for={`${field.props.id}-${i()}`}\n                      class=\"form-check-label\"\n                    >\n                      {favoriteFood.label}\n                    </label>\n                  </div>\n                )}\n              </For>\n              <Show when={field.helpers.error}>\n                <div class=\"invalid-feedback\">{field.helpers.errorMessage}</div>\n              </Show>\n            </>\n          )}\n        />\n      </div>\n      <div class=\"mb-3\">\n        <button class=\"btn btn-primary me-2\">Submit</button>\n        <button class=\"btn btn-primary me-2\" onClick={reset} type=\"button\">\n          Reset\n        </button>\n        <button class=\"btn btn-primary me-2\" onClick={fill} type=\"button\">\n          Fill\n        </button>\n      </div>\n      <p>\n        <b>Form data:</b>\n      </p>\n      <pre class=\"mt-3 border bg-light p-3\">\n        <code>{JSON.stringify(formData(), null, 2)}</code>\n      </pre>\n    </form>\n  );\n};\n";

export { index as default };
