const index = "import { Component } from 'solid-js';\nimport { useFormHandler } from 'solid-form-handler';\nimport { zodSchema } from 'solid-form-handler/zod';\nimport { userSchema } from './schema';\nimport { Checkbox, Checkboxes, Radios, Select, TextInput } from '@components';\n\nexport const UserForm: Component = () => {\n  const formHandler = useFormHandler(zodSchema(userSchema));\n  const { formData } = formHandler;\n\n  const submit = async (event: Event) => {\n    event.preventDefault();\n    try {\n      await formHandler.validateForm();\n      alert('Data sent with success: ' + JSON.stringify(formData()));\n      formHandler.resetForm();\n    } catch (error) {\n      console.error(error);\n    }\n  };\n\n  const fill = () => {\n    formHandler.fillForm({\n      name: 'John',\n      email: 'john@mail.com',\n      country: 2,\n      favoriteFoods: [3, 4],\n      gender: 'female',\n      subscribed: true,\n    });\n  };\n\n  const reset = () => {\n    formHandler.resetForm();\n  };\n\n  return (\n    <>\n      <form autocomplete=\"off\" onSubmit={submit}>\n        <h4 class=\"mb-3\">Using zod schema</h4>\n        <div class=\"mb-3\">\n          <TextInput label=\"Name\" name=\"name\" formHandler={formHandler} />\n        </div>\n        <div class=\"mb-3\">\n          <TextInput label=\"Email\" name=\"email\" formHandler={formHandler} />\n        </div>\n        <div class=\"mb-3\">\n          <Select\n            label=\"Country\"\n            name=\"country\"\n            placeholder=\"Select a country\"\n            options={[\n              { value: 1, label: 'France' },\n              { value: 2, label: 'Spain' },\n              { value: 3, label: 'Canada' },\n            ]}\n            formHandler={formHandler}\n          />\n        </div>\n        <div class=\"mb-3\">\n          <Checkboxes\n            label=\"Favorite foods\"\n            name=\"favoriteFoods\"\n            formHandler={formHandler}\n            options={[\n              { value: 1, label: 'Pizza' },\n              { value: 2, label: 'Hamburger' },\n              { value: 3, label: 'Spaghetti' },\n              { value: 4, label: 'Hot Dog' },\n            ]}\n          />\n        </div>\n        <div class=\"mb-3\">\n          <Radios\n            label=\"Gender\"\n            name=\"gender\"\n            formHandler={formHandler}\n            options={[\n              { value: 'male', label: 'Male' },\n              { value: 'female', label: 'Female' },\n              { value: 'other', label: 'Other' },\n            ]}\n          />\n        </div>\n        <div class=\"mb-3\">\n          <Checkbox\n            label=\"Subscribe to newsletter\"\n            name=\"subscribed\"\n            formHandler={formHandler}\n          />\n        </div>\n        <div class=\"mb-3 w-100\">\n          <button class=\"btn btn-primary me-2 mt-2\">Submit</button>\n          <button\n            class=\"btn btn-primary me-2 mt-2\"\n            disabled={formHandler.isFormInvalid()}\n          >\n            Submit\n          </button>\n          <button\n            class=\"btn btn-primary me-2 mt-2\"\n            onClick={fill}\n            type=\"button\"\n          >\n            Fill\n          </button>\n          <button\n            class=\"btn btn-primary me-2 mt-2\"\n            onClick={reset}\n            type=\"button\"\n          >\n            Reset\n          </button>\n        </div>\n      </form>\n      <p class=\"mt-5\">\n        <b>Form data:</b>\n      </p>\n      <pre class=\"mt-3 border bg-light p-3\">\n        <code>{JSON.stringify(formData(), null, 2)}</code>\n      </pre>\n    </>\n  );\n};\n";

export { index as default };
