const index = "import { Component } from 'solid-js';\nimport { useFormHandler } from 'solid-form-handler';\nimport { zodSchema } from 'solid-form-handler/zod';\nimport { z } from 'zod';\nimport { TextInput } from '@components';\n\nconst schema = z.object({\n  firstName: z.string().min(1, 'First name is required'),\n  lastName: z.string().min(1, 'Last name is required'),\n  email: z.string().email().min(1, 'Email is required'),\n  age: z.coerce.number().min(1, 'Age is required'),\n});\n\nexport const Form: Component = () => {\n  const formHandler = useFormHandler(zodSchema(schema));\n  const { formData } = formHandler;\n\n  const submit = async (event: Event) => {\n    event.preventDefault();\n    try {\n      await formHandler.validateForm();\n      alert('Data sent with success: ' + JSON.stringify(formData()));\n      formHandler.resetForm();\n    } catch (error) {\n      console.error(error);\n    }\n  };\n\n  const reset = () => {\n    formHandler.resetForm();\n  };\n\n  const fill = () => {\n    formHandler.fillForm({\n      firstName: 'John',\n      lastName: 'Wick',\n      email: 'test@mail.com',\n      age: 42,\n    });\n  };\n\n  return (\n    <form autocomplete=\"off\" onSubmit={submit}>\n      <h4 class=\"mb-3\">Using zod schema</h4>\n      <div class=\"mb-3\">\n        <TextInput\n          label=\"First Name\"\n          name=\"firstName\"\n          formHandler={formHandler}\n        />\n      </div>\n      <div class=\"mb-3\">\n        <TextInput\n          label=\"Last Name\"\n          name=\"lastName\"\n          formHandler={formHandler}\n        />\n      </div>\n      <div class=\"mb-3\">\n        <TextInput label=\"Email\" name=\"email\" formHandler={formHandler} />\n      </div>\n      <div class=\"mb-3\">\n        <TextInput label=\"Age\" name=\"age\" formHandler={formHandler} />\n      </div>\n      <div class=\"mb-3\">\n        <button class=\"btn btn-primary me-2\">Submit</button>\n        <button class=\"btn btn-primary me-2\" onClick={reset} type=\"button\">\n          Reset\n        </button>\n        <button class=\"btn btn-primary me-2\" onClick={fill} type=\"button\">\n          Fill\n        </button>\n      </div>\n      <p>\n        <b>Form data:</b>\n      </p>\n      <pre class=\"mt-3 border bg-light p-3\">\n        <code>{JSON.stringify(formData(), null, 2)}</code>\n      </pre>\n    </form>\n  );\n};\n";

export { index as default };
